import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Send, Loader2, CheckCircle, XCircle, Database, MessageSquare, Settings, BarChart3, AlertCircle, Calendar, MapPin, Clock, ExternalLink, DollarSign } from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('chat');
  const [metrics, setMetrics] = useState(null);
  const [systemStatus, setSystemStatus] = useState(null);
  const [knowledgeStats, setKnowledgeStats] = useState(null);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Fetch metrics periodically
  useEffect(() => {
    fetchMetrics();
    fetchSystemStatus();
    fetchKnowledgeStats();

    const interval = setInterval(() => {
      fetchMetrics();
      fetchSystemStatus();
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  const fetchMetrics = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/metrics`);
      setMetrics(response.data);
    } catch (err) {
      console.error('Error fetching metrics:', err);
    }
  };

  const fetchSystemStatus = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/status`);
      setSystemStatus(response.data);
    } catch (err) {
      console.error('Error fetching status:', err);
      setError('Backend server not responding. Please start the server.');
    }
  };

  const fetchKnowledgeStats = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/knowledge/stats`);
      setKnowledgeStats(response.data);
    } catch (err) {
      console.error('Error fetching knowledge stats:', err);
    }
  };

  const handleSendMessage = async () => {
    if (!input.trim() || loading) return;

    const userMessage = {
      role: 'user',
      content: input,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/api/chat`, {
        message: input,
        page: 1,
        page_size: 10
      });

      const assistantMessage = {
        role: 'assistant',
        content: JSON.stringify(response.data),
        timestamp: new Date().toISOString(),
        data: response.data
      };

      setMessages(prev => [...prev, assistantMessage]);
      fetchMetrics();
    } catch (err) {
      console.error('Error sending message:', err);
      setError(err.response?.data?.detail || 'Failed to send message. Please check if the backend is running on port 8000.');

      const errorMessage = {
        role: 'assistant',
        content: 'I apologize, but I encountered an error processing your request. Please make sure the backend server is running and try again.',
        timestamp: new Date().toISOString(),
        error: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString('en-US', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const EventCard = ({ event }) => (
    <div className="event-card">
      {event.poster && (
        <div className="event-poster">
          <img src={event.poster} alt={event.title} />
        </div>
      )}
      <div className="event-content">
        <h4 className="event-title">{event.title}</h4>

        <div className="event-details">
          {event.start_date && (
            <div className="event-detail">
              <Calendar size={16} />
              <span>{event.start_date}</span>
              {event.start_time && <span> at {event.start_time}</span>}
            </div>
          )}

          {event.venue && (
            <div className="event-detail">
              <MapPin size={16} />
              <span>{event.venue}</span>
            </div>
          )}

          {event.address && (
            <div className="event-detail">
              <MapPin size={16} />
              <span className="event-address">{event.address}</span>
            </div>
          )}

          {event.price && (
            <div className="event-detail">
              <DollarSign size={16} />
              <span>{event.price}</span>
            </div>
          )}
        </div>

        <div className="event-footer">
          <span className="event-source">{event.source}</span>
          {event.url && (
            <a 
              href={event.url} 
              target="_blank" 
              rel="noopener noreferrer" 
              className="event-link"
            >
              View Details <ExternalLink size={14} />
            </a>
          )}
        </div>
      </div>
    </div>
  );

  const AssistantMessage = ({ message }) => {
    if (message.error) {
      return (
        <div className="message assistant">
          <div className="message-content error">
            <p className="message-text">{message.content}</p>
          </div>
        </div>
      );
    }

    if (!message.data) {
      return (
        <div className="message assistant">
          <div className="message-content">
            <p className="message-text">{message.content}</p>
          </div>
        </div>
      );
    }

    const { intent, query, location, total_results, results } = message.data;

    return (
      <div className="message assistant">
        <div className="message-content assistant-response">
          <div className="message-header">
            <span className="message-role">AI Assistant</span>
            <span className="message-time">{formatTimestamp(message.timestamp)}</span>
          </div>

          <div className="response-summary">
            <p>
              Found <strong>{total_results}</strong> {intent}(s) {location !== 'Global' && `in ${location}`}
            </p>
          </div>

          {results && results.length > 0 ? (
            <div className="events-grid">
              {results.map((event, idx) => (
                <EventCard key={idx} event={event} />
              ))}
            </div>
          ) : (
            <p className="no-results">No results found. Try a different query.</p>
          )}

          <div className="response-metadata">
            <span className="metadata-tag">Intent: {intent}</span>
            <span className="metadata-tag">Location: {location}</span>
            <span className="metadata-tag">{total_results} results</span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="app">
      <div className="container">
        <div className="card">
          <div className="header">
            <h1>SuperExpat AI Agent System</h1>
            <p className="subtitle">Find Events, Jobs & More with AI</p>
          </div>

          {error && (
            <div className="alert alert-error">
              <AlertCircle size={20} />
              <span>{error}</span>
            </div>
          )}

          <div className="tabs">
            <button
              onClick={() => setActiveTab('chat')}
              className={`tab ${activeTab === 'chat' ? 'active' : ''}`}
            >
              <MessageSquare size={20} />
              Agent Chat
            </button>
            <button
              onClick={() => setActiveTab('metrics')}
              className={`tab ${activeTab === 'metrics' ? 'active' : ''}`}
            >
              <BarChart3 size={20} />
              Metrics
            </button>
            <button
              onClick={() => setActiveTab('system')}
              className={`tab ${activeTab === 'system' ? 'active' : ''}`}
            >
              <Settings size={20} />
              System
            </button>
          </div>

          <div className="content">
            {activeTab === 'chat' && (
              <div className="chat-container">
                {systemStatus && (
                  <div className={`status-banner ${systemStatus.status === 'ok' ? 'operational' : 'degraded'}`}>
                    <div className="status-left">
                      {systemStatus.status === 'ok' ? (
                        <CheckCircle size={20} />
                      ) : (
                        <XCircle size={20} />
                      )}
                      <span>
                        {systemStatus.status === 'ok' 
                          ? 'All systems operational' 
                          : 'System degraded'}
                      </span>
                    </div>
                    {metrics && (
                      <div className="status-right">
                        Avg Response: {Math.round(metrics.avg_response_ms)}ms
                      </div>
                    )}
                  </div>
                )}

                <div className="messages">
                  {messages.length === 0 ? (
                    <div className="empty-state">
                      <MessageSquare size={48} />
                      <h3>Welcome to SuperExpat AI Assistant</h3>
                      <p>Ask me about events, jobs, or activities in any city!</p>
                      <div className="sample-queries">
                        <button onClick={() => setInput('events in London')}>
                          Events in London
                        </button>
                        <button onClick={() => setInput('jobs in Berlin')}>
                          Jobs in Berlin
                        </button>
                        <button onClick={() => setInput('concerts in New York')}>
                          Concerts in New York
                        </button>
                        <button onClick={() => setInput('festivals in Paris')}>
                          Festivals in Paris
                        </button>
                      </div>
                    </div>
                  ) : (
                    messages.map((msg, idx) => (
                      msg.role === 'user' ? (
                        <div key={idx} className="message user">
                          <div className="message-content">
                            <div className="message-header">
                              <span className="message-role">You</span>
                              <span className="message-time">{formatTimestamp(msg.timestamp)}</span>
                            </div>
                            <p className="message-text">{msg.content}</p>
                          </div>
                        </div>
                      ) : (
                        <AssistantMessage key={idx} message={msg} />
                      )
                    ))
                  )}
                  {loading && (
                    <div className="message assistant">
                      <div className="message-content">
                        <div className="typing-indicator">
                          <Loader2 className="spin" size={20} />
                          <span>Searching for results...</span>
                        </div>
                      </div>
                    </div>
                  )}
                  <div ref={messagesEndRef} />
                </div>

                <div className="input-container">
                  <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Try: 'events in London' or 'jobs in Berlin'"
                    disabled={loading}
                    className="input-field"
                  />
                  <button
                    onClick={handleSendMessage}
                    disabled={loading || !input.trim()}
                    className="send-button"
                  >
                    {loading ? <Loader2 className="spin" size={20} /> : <Send size={20} />}
                  </button>
                </div>
              </div>
            )}

            {activeTab === 'metrics' && (
              <div className="metrics-container">
                <h2>System Status</h2>
                {metrics ? (
                  <div className="metrics-grid">
                    <div className="metric-card blue">
                      <div className="metric-label">System Status</div>
                      <div className="metric-value">{metrics.status}</div>
                    </div>
                    <div className="metric-card green">
                      <div className="metric-label">Avg Response</div>
                      <div className="metric-value">{Math.round(metrics.avg_response_ms)}ms</div>
                    </div>
                  </div>
                ) : (
                  <div className="loading-state">
                    <Loader2 className="spin" size={32} />
                    <p>Loading metrics...</p>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'system' && (
              <div className="system-container">
                <h2>System Architecture</h2>
                <div className="tech-stack">
                  <h3>Technical Stack</h3>
                  <ul>
                    <li> FastAPI Backend with CORS</li>
                    <li> React Frontend with Modern UI</li>
                    <li> SerpAPI & Eventbrite Integration</li>
                    <li> Intent Classification & Location Extraction</li>
                    <li> Real-time Event & Job Search</li>
                  </ul>
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="footer">
          <p>SuperExpat AI Agent - Events & Jobs Discovery Platform</p>
        </div>
      </div>
    </div>
  );
}

export default App;
