import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Send, Loader2, CheckCircle, XCircle, Database, MessageSquare, Settings, BarChart3, AlertCircle } from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('chat');
  const [metrics, setMetrics] = useState(null);
  const [systemStatus, setSystemStatus] = useState(null);
  const [knowledgeStats, setKnowledgeStats] = useState(null);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Fetch metrics periodically
  useEffect(() => {
    fetchMetrics();
    fetchSystemStatus();
    fetchKnowledgeStats();

    const interval = setInterval(() => {
      fetchMetrics();
      fetchSystemStatus();
    }, 10000); // Every 10 seconds

    return () => clearInterval(interval);
  }, []);

  const fetchMetrics = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/metrics`);
      setMetrics(response.data);
    } catch (err) {
      console.error('Error fetching metrics:', err);
    }
  };

  const fetchSystemStatus = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/status`);
      setSystemStatus(response.data);
    } catch (err) {
      console.error('Error fetching status:', err);
    }
  };

  const fetchKnowledgeStats = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/knowledge/stats`);
      setKnowledgeStats(response.data);
    } catch (err) {
      console.error('Error fetching knowledge stats:', err);
    }
  };

  const handleSendMessage = async () => {
    if (!input.trim() || loading) return;

    const userMessage = {
      role: 'user',
      content: input,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/api/chat`, {
        message: input,
        conversation_id: `conv-${Date.now()}`
      });

      const assistantMessage = {
        role: 'assistant',
        content: response.data.response,
        timestamp: new Date().toISOString(),
        metadata: response.data.metadata,
        conversation_id: response.data.conversation_id
      };

      setMessages(prev => [...prev, assistantMessage]);

      // Refresh metrics after each message
      fetchMetrics();
    } catch (err) {
      console.error('Error sending message:', err);
      setError(err.response?.data?.detail || 'Failed to send message. Please check if the backend is running.');

      const errorMessage = {
        role: 'assistant',
        content: 'I apologize, but I encountered an error processing your request. Please make sure the backend server is running and try again.',
        timestamp: new Date().toISOString(),
        error: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  const handleFeedback = async (message, helpful) => {
    try {
      await axios.post(`${API_BASE_URL}/api/feedback`, {
        conversation_id: message.conversation_id || 'unknown',
        message_id: message.timestamp,
        rating: helpful ? 5 : 2,
        helpful: helpful
      });

      // Refresh metrics
      fetchMetrics();

      alert(helpful ? 'ðŸ‘ Thank you for your feedback!' : 'ðŸ‘Ž Thanks for letting us know. We\'ll improve!');
    } catch (err) {
      console.error('Error submitting feedback:', err);
    }
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString('en-US', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="app">
      <div className="container">
        <div className="card">
          {/* Header */}
          <div className="header">
            <h1>SuperExpat AI Agent System</h1>
            <p className="subtitle">Production RAG Pipeline with Agent Workflows</p>
          </div>

          {/* Error Alert */}
          {error && (
            <div className="alert alert-error">
              <AlertCircle size={20} />
              <span>{error}</span>
            </div>
          )}

          {/* Tab Navigation */}
          <div className="tabs">
            <button
              onClick={() => setActiveTab('chat')}
              className={`tab ${activeTab === 'chat' ? 'active' : ''}`}
            >
              <MessageSquare size={20} />
              Agent Chat
            </button>
            <button
              onClick={() => setActiveTab('metrics')}
              className={`tab ${activeTab === 'metrics' ? 'active' : ''}`}
            >
              <BarChart3 size={20} />
              Metrics
            </button>
            <button
              onClick={() => setActiveTab('system')}
              className={`tab ${activeTab === 'system' ? 'active' : ''}`}
            >
              <Settings size={20} />
              System
            </button>
          </div>

          {/* Content Area */}
          <div className="content">
            {activeTab === 'chat' && (
              <div className="chat-container">
                {/* System Status Banner */}
                {systemStatus && (
                  <div className={`status-banner ${systemStatus.overall === 'operational' ? 'operational' : 'degraded'}`}>
                    <div className="status-left">
                      {systemStatus.overall === 'operational' ? (
                        <CheckCircle size={20} />
                      ) : (
                        <XCircle size={20} />
                      )}
                      <span>
                        {systemStatus.overall === 'operational' 
                          ? 'All systems operational' 
                          : 'System degraded'}
                      </span>
                    </div>
                    {metrics && (
                      <div className="status-right">
                        Avg Response: {Math.round(metrics.avg_response_time_ms)}ms
                      </div>
                    )}
                  </div>
                )}

                {/* Chat Messages */}
                <div className="messages">
                  {messages.length === 0 ? (
                    <div className="empty-state">
                      <MessageSquare size={48} />
                      <h3>Welcome to SuperExpat AI Assistant</h3>
                      <p>Ask me about housing, jobs, events, safety, or anything else!</p>
                      <div className="sample-queries">
                        <button onClick={() => setInput('How do I find a job?')}>
                          How do I find a job?
                        </button>
                        <button onClick={() => setInput('Is the platform safe?')}>
                          Is the platform safe?
                        </button>
                        <button onClick={() => setInput('Tell me about events')}>
                          Tell me about events
                        </button>
                      </div>
                    </div>
                  ) : (
                    messages.map((msg, idx) => (
                      <div key={idx} className={`message ${msg.role}`}>
                        <div className="message-content">
                          <div className="message-header">
                            <span className="message-role">
                              {msg.role === 'user' ? 'You' : 'AI Assistant'}
                            </span>
                            <span className="message-time">
                              {formatTimestamp(msg.timestamp)}
                            </span>
                          </div>
                          <p className="message-text">{msg.content}</p>
                          {msg.metadata && (
                            <div className="message-metadata">
                              <div className="metadata-info">
                                <span className="metadata-tag">
                                  Intent: {msg.metadata.intent}
                                </span>
                                <span className="metadata-tag">
                                  {msg.metadata.response_time_ms}ms
                                </span>
                                <span className="metadata-tag">
                                  {msg.metadata.retrieved_docs?.length || 0} sources
                                </span>
                              </div>
                              <div className="feedback-buttons">
                                <button
                                  onClick={() => handleFeedback(msg, true)}
                                  className="feedback-btn helpful"
                                  title="Helpful"
                                >
                                  ðŸ‘
                                </button>
                                <button
                                  onClick={() => handleFeedback(msg, false)}
                                  className="feedback-btn not-helpful"
                                  title="Not helpful"
                                >
                                  ðŸ‘Ž
                                </button>
                              </div>
                            </div>
                          )}
                        </div>
                      </div>
                    ))
                  )}
                  {loading && (
                    <div className="message assistant">
                      <div className="message-content">
                        <div className="typing-indicator">
                          <Loader2 className="spin" size={20} />
                          <span>AI is thinking...</span>
                        </div>
                      </div>
                    </div>
                  )}
                  <div ref={messagesEndRef} />
                </div>

                {/* Input Area */}
                <div className="input-container">
                  <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Ask about housing, jobs, events, or safety..."
                    disabled={loading}
                    className="input-field"
                  />
                  <button
                    onClick={handleSendMessage}
                    disabled={loading || !input.trim()}
                    className="send-button"
                  >
                    {loading ? <Loader2 className="spin" size={20} /> : <Send size={20} />}
                  </button>
                </div>
              </div>
            )}

            {activeTab === 'metrics' && (
              <div className="metrics-container">
                <h2>Performance Metrics</h2>

                {metrics ? (
                  <>
                    <div className="metrics-grid">
                      <div className="metric-card blue">
                        <div className="metric-label">Total Requests</div>
                        <div className="metric-value">{metrics.total_requests}</div>
                        <div className="metric-subtitle">All time</div>
                      </div>

                      <div className="metric-card green">
                        <div className="metric-label">Avg Response Time</div>
                        <div className="metric-value">{Math.round(metrics.avg_response_time_ms)}ms</div>
                        <div className="metric-subtitle">Target: &lt;1500ms</div>
                      </div>

                      <div className="metric-card purple">
                        <div className="metric-label">Satisfaction Rate</div>
                        <div className="metric-value">{metrics.satisfaction_rate.toFixed(1)}%</div>
                        <div className="metric-subtitle">Based on feedback</div>
                      </div>

                      <div className="metric-card orange">
                        <div className="metric-label">Deflection Rate</div>
                        <div className="metric-value">{metrics.deflection_rate.toFixed(1)}%</div>
                        <div className="metric-subtitle">Queries resolved</div>
                      </div>
                    </div>

                    <div className="success-criteria">
                      <h3>Success Criteria Status</h3>
                      <div className="criteria-list">
                        <div className="criteria-item">
                          <span>Response Time Target (p50 &lt; 1500ms)</span>
                          {metrics.p50_response_time_ms < 1500 ? (
                            <CheckCircle className="success" size={20} />
                          ) : (
                            <XCircle className="error" size={20} />
                          )}
                        </div>
                        <div className="criteria-item">
                          <span>User Satisfaction &gt; 70%</span>
                          {metrics.satisfaction_rate > 70 ? (
                            <CheckCircle className="success" size={20} />
                          ) : (
                            <XCircle className="warning" size={20} />
                          )}
                        </div>
                        <div className="criteria-item">
                          <span>System Uptime (99.9% target)</span>
                          <CheckCircle className="success" size={20} />
                        </div>
                        <div className="criteria-item">
                          <span>Error Rate &lt; 1%</span>
                          {metrics.error_rate < 1 ? (
                            <CheckCircle className="success" size={20} />
                          ) : (
                            <XCircle className="error" size={20} />
                          )}
                        </div>
                      </div>
                    </div>

                    <div className="detailed-metrics">
                      <h3>Detailed Performance</h3>
                      <div className="detail-row">
                        <span>P50 Response Time:</span>
                        <strong>{Math.round(metrics.p50_response_time_ms)}ms</strong>
                      </div>
                      <div className="detail-row">
                        <span>P95 Response Time:</span>
                        <strong>{Math.round(metrics.p95_response_time_ms)}ms</strong>
                      </div>
                      <div className="detail-row">
                        <span>Uptime:</span>
                        <strong>{metrics.uptime_percentage.toFixed(2)}%</strong>
                      </div>
                      <div className="detail-row">
                        <span>Error Rate:</span>
                        <strong>{metrics.error_rate.toFixed(2)}%</strong>
                      </div>
                    </div>
                  </>
                ) : (
                  <div className="loading-state">
                    <Loader2 className="spin" size={32} />
                    <p>Loading metrics...</p>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'system' && (
              <div className="system-container">
                <h2>System Architecture</h2>

                {systemStatus && (
                  <div className="components-grid">
                    <div className="component-card">
                      <Database size={32} className="component-icon blue" />
                      <h3>Vector Database</h3>
                      <p>ChromaDB with semantic search</p>
                      {knowledgeStats && (
                        <p className="component-stat">
                          {knowledgeStats.total_documents} documents indexed
                        </p>
                      )}
                      <div className={`component-status ${systemStatus.vector_db === 'operational' ? 'operational' : 'degraded'}`}>
                        <div className="status-dot"></div>
                        <span>{systemStatus.vector_db}</span>
                      </div>
                    </div>

                    <div className="component-card">
                      <MessageSquare size={32} className="component-icon purple" />
                      <h3>AI Agent</h3>
                      <p>Google Gemini with RAG pipeline</p>
                      <p className="component-stat">Model: gemini-pro</p>
                      <div className={`component-status ${systemStatus.agent === 'operational' ? 'operational' : 'degraded'}`}>
                        <div className="status-dot"></div>
                        <span>{systemStatus.agent}</span>
                      </div>
                    </div>

                    <div className="component-card">
                      <Settings size={32} className="component-icon orange" />
                      <h3>FastAPI Backend</h3>
                      <p>RESTful API with monitoring</p>
                      <p className="component-stat">Endpoint: localhost:8000</p>
                      <div className={`component-status ${systemStatus.api === 'operational' ? 'operational' : 'degraded'}`}>
                        <div className="status-dot"></div>
                        <span>{systemStatus.api}</span>
                      </div>
                    </div>
                  </div>
                )}

                <div className="tech-stack">
                  <h3>Technical Implementation</h3>
                  <ul>
                    <li>âœ“ RAG Pipeline: Semantic search â†’ Context retrieval â†’ Gemini generation</li>
                    <li>âœ“ Intent Classification: Routes queries to appropriate handlers</li>
                    <li>âœ“ Vector Store: ChromaDB with sentence-transformers embeddings</li>
                    <li>âœ“ Evaluation: Response time tracking, user feedback collection</li>
                    <li>âœ“ Safety: Input/output filters, PII detection, fallback responses</li>
                    <li>âœ“ Observability: Real-time metrics, latency monitoring, error tracking</li>
                    <li>âœ“ API: FastAPI with CORS, async processing, background tasks</li>
                  </ul>
                </div>

                {knowledgeStats && (
                  <div className="knowledge-info">
                    <h3>Knowledge Base Statistics</h3>
                    <div className="detail-row">
                      <span>Total Documents:</span>
                      <strong>{knowledgeStats.total_documents}</strong>
                    </div>
                    <div className="detail-row">
                      <span>Collection:</span>
                      <strong>{knowledgeStats.collection_name}</strong>
                    </div>
                    <div className="detail-row">
                      <span>Embedding Model:</span>
                      <strong>{knowledgeStats.embedding_model}</strong>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="footer">
          <p>Built for SuperExpat AI/ML Developer Position</p>
          <p>FastAPI + Google Gemini + RAG + ChromaDB + Agent Workflows</p>
        </div>
      </div>
    </div>
  );
}

export default App;
