// ============================================================================
// IMMEDIATE FIX #2: Stable UI with Debouncing + "Date TBA" Display
// File: frontend/src/App.jsx
// ============================================================================

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Send, Loader2, CheckCircle, XCircle, Database, MessageSquare, Settings, BarChart3, AlertCircle, Calendar, MapPin, Clock, ExternalLink, DollarSign, Briefcase } from 'lucide-react';
import './App.css';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('chat');
  const [metrics, setMetrics] = useState(null);
  const [systemStatus, setSystemStatus] = useState(null);
  const messagesEndRef = useRef(null);

  // Debounce for search (prevent flickering)
  const [debouncedInput, setDebouncedInput] = useState('');

  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedInput(input);
    }, 300); // Wait 300ms after user stops typing

    return () => clearTimeout(timer);
  }, [input]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    fetchMetrics();
    fetchSystemStatus();

    const interval = setInterval(() => {
      fetchMetrics();
      fetchSystemStatus();
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  const fetchMetrics = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/metrics`);
      setMetrics(response.data);
    } catch (err) {
      console.error('Error fetching metrics:', err);
    }
  };

  const fetchSystemStatus = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/status`);
      setSystemStatus(response.data);
    } catch (err) {
      console.error('Error fetching status:', err);
    }
  };

  const handleSendMessage = async (queryText) => {
    const finalQuery = queryText || input;
    if (!finalQuery.trim() || loading) return;

    const userMessage = {
      role: 'user',
      content: finalQuery,
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const response = await axios.post(`${API_BASE_URL}/api/chat`, {
        message: finalQuery,
        page: 1,
        page_size: 20  // Get more results
      });

      const assistantMessage = {
        role: 'assistant',
        content: JSON.stringify(response.data),
        timestamp: new Date().toISOString(),
        data: response.data
      };

      setMessages(prev => [...prev, assistantMessage]);
      fetchMetrics();
    } catch (err) {
      console.error('Error sending message:', err);

      const errorMessage = {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please make sure the backend server is running.',
        timestamp: new Date().toISOString(),
        error: true
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString('en-US', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (dateStr) => {
    if (!dateStr) return null;
    try {
      const date = new Date(dateStr);
      return date.toLocaleDateString('en-US', { 
        weekday: 'short',
        month: 'short', 
        day: 'numeric',
        year: 'numeric'
      });
    } catch {
      return dateStr;
    }
  };

  const EventCard = ({ event }) => {
    const displayDate = event.start_date 
      ? formatDate(event.start_date)
      : "Date To Be Announced";

    const isDateUnknown = !event.start_date;

    return (
      <div className="event-card">
        <div className="event-poster">
          {event.poster ? (
            <img 
              src={event.poster} 
              alt={event.title} 
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.parentElement.classList.add('no-image');
              }} 
            />
          ) : (
            <div className="no-image-placeholder">
              {event.type === 'job' ? <Briefcase size={48} /> : <Calendar size={48} />}
            </div>
          )}
        </div>

        <div className="event-content">
          <div className="event-type-badge">
            {event.type === 'job' ? 'ðŸ’¼ Job' : 'ðŸŽ‰ Event'}
          </div>

          <h4 className="event-title">{event.title}</h4>

          <div className="event-details">
            <div className="event-detail">
              <Calendar size={16} />
              <span className={isDateUnknown ? 'date-tba' : ''}>
                {displayDate}
                {event.start_time && !isDateUnknown && ` â€¢ ${event.start_time}`}
              </span>
            </div>

            {event.venue && (
              <div className="event-detail">
                <MapPin size={16} />
                <span>{event.venue}</span>
              </div>
            )}

            {event.company && (
              <div className="event-detail">
                <Briefcase size={16} />
                <span>{event.company}</span>
              </div>
            )}

            {event.address && !event.venue && (
              <div className="event-detail">
                <MapPin size={16} />
                <span className="event-address">{event.address}</span>
              </div>
            )}

            {event.price && (
              <div className="event-detail">
                <DollarSign size={16} />
                <span>{event.price}</span>
              </div>
            )}
          </div>

          <div className="event-footer">
            {event.url && (
              <a 
                href={event.url} 
                target="_blank" 
                rel="noopener noreferrer" 
                className="event-link"
              >
                View Details <ExternalLink size={14} />
              </a>
            )}
          </div>
        </div>
      </div>
    );
  };

  const AssistantMessage = ({ message }) => {
    if (message.error) {
      return (
        <div className="message assistant">
          <div className="message-content error">
            <p className="message-text">{message.content}</p>
          </div>
        </div>
      );
    }

    if (!message.data) {
      return (
        <div className="message assistant">
          <div className="message-content">
            <p className="message-text">{message.content}</p>
          </div>
        </div>
      );
    }

    const { intent, query, location, total_results, results } = message.data;

    return (
      <div className="message assistant">
        <div className="message-content assistant-response">
          <div className="message-header">
            <span className="message-role">AI Assistant</span>
            <span className="message-time">{formatTimestamp(message.timestamp)}</span>
          </div>

          <div className="response-summary">
            <p>
              Found <strong>{total_results}</strong> {intent === 'job' ? 'job(s)' : 'event(s)'}
              {location !== 'Global' && ` in ${location}`}
            </p>
          </div>

          {results && results.length > 0 ? (
            <div className="events-grid">
              {results.map((event, idx) => (
                <EventCard key={event.id || idx} event={event} />
              ))}
            </div>
          ) : (
            <div className="no-results">
              <AlertCircle size={24} />
              <p>No results found. Try a different query or location.</p>
            </div>
          )}

          <div className="response-metadata">
            <span className="metadata-tag">Intent: {intent}</span>
            <span className="metadata-tag">Location: {location}</span>
            <span className="metadata-tag">{total_results} results</span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="app">
      <div className="container">
        <div className="card">
          <div className="header">
            <h1>SuperExpat AI Agent System</h1>
            <p className="subtitle">Find Events & Jobs with AI-Powered Search</p>
          </div>

          <div className="tabs">
            <button
              onClick={() => setActiveTab('chat')}
              className={`tab ${activeTab === 'chat' ? 'active' : ''}`}
            >
              <MessageSquare size={20} />
              Agent Chat
            </button>
            <button
              onClick={() => setActiveTab('metrics')}
              className={`tab ${activeTab === 'metrics' ? 'active' : ''}`}
            >
              <BarChart3 size={20} />
              Metrics
            </button>
            <button
              onClick={() => setActiveTab('system')}
              className={`tab ${activeTab === 'system' ? 'active' : ''}`}
            >
              <Settings size={20} />
              System
            </button>
          </div>

          <div className="content">
            {activeTab === 'chat' && (
              <div className="chat-container">
                {systemStatus && (
                  <div className={`status-banner ${systemStatus.status === 'ok' ? 'operational' : 'degraded'}`}>
                    <div className="status-left">
                      {systemStatus.status === 'ok' ? <CheckCircle size={20} /> : <XCircle size={20} />}
                      <span>
                        {systemStatus.status === 'ok' ? 'All systems operational' : 'System degraded'}
                      </span>
                    </div>
                    {metrics && (
                      <div className="status-right">
                        Avg Response: {Math.round(metrics.avg_response_ms)}ms
                      </div>
                    )}
                  </div>
                )}

                <div className="messages">
                  {messages.length === 0 ? (
                    <div className="empty-state">
                      <MessageSquare size={48} />
                      <h3>Welcome to SuperExpat AI Assistant</h3>
                      <p>Find events, jobs, and opportunities anywhere in the world!</p>
                      <div className="sample-queries">
                        <button onClick={() => handleSendMessage('events in London')}>
                          Events in London
                        </button>
                        <button onClick={() => handleSendMessage('jobs in Berlin')}>
                          Jobs in Berlin
                        </button>
                        <button onClick={() => handleSendMessage('concerts in New York')}>
                          Concerts in New York
                        </button>
                        <button onClick={() => handleSendMessage('festivals in Paris')}>
                          Festivals in Paris
                        </button>
                      </div>
                    </div>
                  ) : (
                    messages.map((msg, idx) => (
                      msg.role === 'user' ? (
                        <div key={idx} className="message user">
                          <div className="message-content">
                            <div className="message-header">
                              <span className="message-role">You</span>
                              <span className="message-time">{formatTimestamp(msg.timestamp)}</span>
                            </div>
                            <p className="message-text">{msg.content}</p>
                          </div>
                        </div>
                      ) : (
                        <AssistantMessage key={idx} message={msg} />
                      )
                    ))
                  )}
                  {loading && (
                    <div className="message assistant">
                      <div className="message-content">
                        <div className="typing-indicator">
                          <Loader2 className="spin" size={20} />
                          <span>Searching...</span>
                        </div>
                      </div>
                    </div>
                  )}
                  <div ref={messagesEndRef} />
                </div>

                <div className="input-container">
                  <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Try: 'events in London' or 'jobs in Berlin'"
                    disabled={loading}
                    className="input-field"
                  />
                  <button
                    onClick={() => handleSendMessage()}
                    disabled={loading || !input.trim()}
                    className="send-button"
                  >
                    {loading ? <Loader2 className="spin" size={20} /> : <Send size={20} />}
                  </button>
                </div>
              </div>
            )}

            {activeTab === 'metrics' && (
              <div className="metrics-container">
                <h2>System Metrics</h2>
                {metrics ? (
                  <div className="metrics-grid">
                    <div className="metric-card blue">
                      <div className="metric-label">System Status</div>
                      <div className="metric-value">{metrics.status}</div>
                    </div>
                    <div className="metric-card green">
                      <div className="metric-label">Avg Response</div>
                      <div className="metric-value">{Math.round(metrics.avg_response_ms)}ms</div>
                    </div>
                  </div>
                ) : (
                  <div className="loading-state">
                    <Loader2 className="spin" size={32} />
                    <p>Loading metrics...</p>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'system' && (
              <div className="system-container">
                <h2>System Architecture</h2>
                <div className="tech-stack">
                  <h3>Technical Stack</h3>
                  <ul>
                    <li>âœ“ FastAPI Backend + CORS</li>
                    <li>âœ“ React Frontend</li>
                    <li>âœ“ SerpAPI & Eventbrite Integration</li>
                    <li>âœ“ Real-time Event & Job Search</li>
                    <li>âœ“ Intent Classification</li>
                  </ul>
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="footer">
          <p>SuperExpat AI Agent - Powered by FastAPI + React + SerpAPI</p>
        </div>
      </div>
    </div>
  );
}

export default App;
